import React, { useState, useEffect } from 'react';
import Navbar from '../components/Auth/Navbar';
import './DashboardAdmin.css';

const API_BASE = 'http://localhost:4000/api';

export default function DashboardAdmin() {
  const [compras, setCompras] = useState([]);
  const [stats, setStats]     = useState({ mes: 0, semana: 0, ano: 0 });
  const token = localStorage.getItem('token');

  // 1) Traer ventas
  useEffect(() => {
    (async () => {
      const res = await fetch(`${API_BASE}/ventas`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setCompras(data);
    })();
  }, [token]);

  // 2) Calcular ingresos
  useEffect(() => {
    const hoy = new Date();
    let mes = 0, sem = 0, ano = 0;
    compras.forEach(c => {
      const f     = new Date(c.fecha),
            monto = parseFloat(c.total);
      if (f.getFullYear() === hoy.getFullYear()) ano += monto;
      if (f.getMonth()    === hoy.getMonth())    mes += monto;
      if ((hoy - f)/(1000*60*60*24) <= 7)        sem += monto;
    });
    setStats({ mes, semana: sem, ano });
  }, [compras]);

  // 3) Solo actualiza el UI al cambiar el select
  const handleEstadoChange = (id, nuevoEstado) => {
    setCompras(prev =>
      prev.map(c =>
        c.id === id
          ? { ...c, estado: nuevoEstado }
          : c
      )
    );
  };

  // 4) EnvÃ­a el PUT al servidor SOLO al hacer clic en "Guardar"
  const actualizar = async (id) => {
    try {
      const compra = compras.find(c => c.id === id);
      const res = await fetch(`${API_BASE}/ventas/${id}/estado`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ estado: compra.estado })
      });
      const data = await res.json();
      if (!res.ok) {
        return alert(data.msg || 'Error actualizando estado');
      }
      alert('Estado actualizado correctamente');
    } catch (e) {
      console.error(e);
      alert('Error al conectar con el servidor');
    }
  };

  const opciones = [
    { value:'pedido',     label:'Pedido'    },
    { value:'enviado',    label:'Enviado'   },
    { value:'en reparto', label:'En reparto'},
    { value:'entregado',  label:'Entregado' }
  ];

  return (
    <div className="dashboard-admin">
      <Navbar/>
      <h2>Dashboard Admin</h2>

      <div className="ingresos-cards">
        <div><h4>Mes</h4><p>${stats.mes.toFixed(2)}</p></div>
        <div><h4>Semana</h4><p>${stats.semana.toFixed(2)}</p></div>
        <div><h4>AÃ±o</h4><p>${stats.ano.toFixed(2)}</p></div>
      </div>

      <table className="tabla-compras">
        <thead>
          <tr>
            <th>ID</th><th>Usuario</th><th>Fecha</th><th>DirecciÃ³n</th>
            <th>Estado</th><th>Pago</th><th>Monto</th><th>AcciÃ³n</th>
          </tr>
        </thead>
        <tbody>
          {compras.map(c => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.usuario.nombres} {c.usuario.apellidos}</td>
              <td>{new Date(c.fecha).toLocaleString()}</td>
              <td>
                {c.direccionDeVenta
                  ? `${c.direccionDeVenta.calle}, ${c.direccionDeVenta.ciudad}`
                  : 'Recoger'}
              </td>
              <td>
                <select
                  value={c.estado}
                  onChange={e => handleEstadoChange(c.id, e.target.value)}
                >
                  {opciones.map(o => (
                    <option key={o.value} value={o.value}>
                      {o.label}
                    </option>
                  ))}
                </select>
              </td>
              <td>{c.metodoDeVenta.tipo}</td>
              <td>${parseFloat(c.total).toFixed(2)}</td>
              <td>
                <button
                  className="btn btn-success"
                  onClick={() => actualizar(c.id)}
                >
                  Guardar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
