import React, { useState, useContext } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';
import logo from '../../assets/img/Logo.png';

import { AuthContext }     from '../../contexts/AuthContext';
import { CartContext }     from '../../contexts/CartContext';
import { PurchaseContext } from '../../contexts/PurchaseContext';

export default function Navbar() {
  const [open, setOpen] = useState(false);
  const location       = useLocation();
  const navigate       = useNavigate();

  const { token, logout } = useContext(AuthContext);
  const { clearCart }     = useContext(CartContext);
  const { clearPurchases }= useContext(PurchaseContext);

  const role = useContext(AuthContext).user?.rol || localStorage.getItem('rol');
  const isLoggedIn = Boolean(token);

  const handleLogout = () => {
    clearCart();
    clearPurchases();
    logout();
    navigate('/login');
    setOpen(false);
  };

  return (
    <header className="navbar">
      <div className="navbar__brand">
        <img src={logo} alt="Logo" className="navbar__logo" onClick={() => setOpen(false)} />
      </div>

      <nav className={`navbar__links ${open ? 'open' : ''}`}>
        {/* Inicio y Cat√°logo para todos los usuarios autenticados */}
        {isLoggedIn && (
          <>
            <Link to="/" onClick={() => setOpen(false)} className={location.pathname === '/' ? 'active' : ''}>Inicio</Link>
            <Link to="/catalogo" onClick={() => setOpen(false)} className={location.pathname.startsWith('/catalogo') ? 'active' : ''}>Cat√°logo</Link>
          </>
        )}

        {/* Carrito y Mis Compras solo para cliente y monitor */}
        {isLoggedIn && (role === 'cliente' || role === 'monitor') && (
          <>
            <Link to="/carrito" onClick={() => setOpen(false)} className={location.pathname === '/carrito' ? 'active' : ''}>üõí Carrito</Link>
            <Link to="/mis-compras" onClick={() => setOpen(false)} className={location.pathname === '/mis-compras' ? 'active' : ''}>üßæ Mis Compras</Link>
          </>
        )}

        {/* Monitoreo y Auditor√≠a solo para monitor */}
        {isLoggedIn && role === 'monitor' && (
          <>
            <Link to="/monitor" onClick={() => setOpen(false)} className={location.pathname === '/monitor' ? 'active' : ''}>Monitoreo</Link>
            <Link to="/auditoria" onClick={() => setOpen(false)} className={location.pathname === '/auditoria' ? 'active' : ''}>Auditor√≠a</Link>
          </>
        )}

        {/* Dashboard Admin solo para admin */}
        {isLoggedIn && role === 'admin' && (
          <Link to="/admin/dashboard" onClick={() => setOpen(false)} className={location.pathname === '/admin/dashboard' ? 'active' : ''}>Dashboard Admin</Link>
        )}

        {isLoggedIn && (
          <button onClick={handleLogout} className="navbar__logout">Cerrar Sesi√≥n</button>
        )}
      </nav>

      <button className={`navbar__hamburger ${open ? 'open' : ''}`} onClick={() => setOpen(o => !o)}>
        <span /><span /><span />
      </button>
    </header>
  );
}
