// src/pages/CarritoPage.jsx
import React, { useEffect, useState } from 'react';
import Navbar from '../components/Auth/Navbar';
import './CarritoPage.css';

export default function CarritoPage() {
  const [carrito, setCarrito]             = useState(null);

  // ‚Äî DIRECCIONES ‚Äî
  const [direcciones, setDirecciones]     = useState([]);
  const [direccion, setDireccion]         = useState({
    nombre:'', calle:'', ciudad:'', estado:'', cp:'', telefono:'', referencia:''
  });
  const [direccionGuardada, setDireccionGuardada] = useState(false);
  const [mensajeDireccion, setMensajeDireccion]   = useState('');

  // ‚Äî M√âTODOS DE PAGO ‚Äî
  const [metodos, setMetodos]           = useState([]);
  const [metodoPago, setMetodoPago]     = useState('');
  const [detallesPago, setDetallesPago] = useState({
    numero:'', expiracion:'', cvv:'', email:''
  });
  const [pagoGuardado, setPagoGuardado] = useState(false);
  const [mensajePago, setMensajePago]   = useState('');

  // ‚Äî ESTADO GENERAL ‚Äî
  const [mensajeFinal, setMensajeFinal] = useState('');
  const token = localStorage.getItem('token');
  const API   = 'http://localhost:4000/api';

  useEffect(() => {
    // 1) Carrito
    fetch(`${API}/carrito`, { headers:{ Authorization:`Bearer ${token}` } })
      .then(r=>r.json()).then(setCarrito).catch(console.error);

    // 2) Direcciones
    fetch(`${API}/direcciones`, { headers:{ Authorization:`Bearer ${token}` } })
      .then(r=>r.json()).then(dirs=>{
        setDirecciones(dirs||[]);
        if(dirs.length){
          const d=dirs[0];
          setDireccion({
            nombre:d.nombre_recibe, calle:d.calle, ciudad:d.ciudad,
            estado:d.estado, cp:d.cp, telefono:d.telefono,
            referencia:d.referencia||''
          });
          setDireccionGuardada(true);
          setMensajeDireccion('üì¶ Direcci√≥n cargada del servidor.');
        }
      }).catch(console.error);

    // 3) M√©todos de pago
    fetch(`${API}/metodos`, { headers:{ Authorization:`Bearer ${token}` } })
      .then(r=>r.json()).then(mps=>{
        setMetodos(mps||[]);
        if(mps.length){
          const m=mps[0];
          setMetodoPago(m.id.toString());
          if(m.tipo==='tarjeta'){
            setDetallesPago({ numero:m.titular, expiracion:'', cvv:'', email:'' });
          } else {
            setDetallesPago({ numero:'', expiracion:'', cvv:'', email:m.titular||'' });
          }
          setPagoGuardado(true);
          setMensajePago('üí≥ M√©todo de pago cargado del servidor.');
        }
      }).catch(console.error);
  },[]);

  if(!carrito) return <p>Cargando carrito‚Ä¶</p>;

  // ‚Äî helpers carrito ‚Äî
  const cambiarCantidad = (id,qty)=>{
    fetch(`${API}/carrito/detalle/${id}`,{
      method:'PUT',
      headers:{
        'Content-Type':'application/json',
        Authorization:`Bearer ${token}`
      },
      body:JSON.stringify({ cantidad:qty })
    }).then(()=>{
      fetch(`${API}/carrito`,{ headers:{ Authorization:`Bearer ${token}` }})
        .then(r=>r.json()).then(setCarrito);
    }).catch(console.error);
  };
  const eliminarItem = id=>cambiarCantidad(id,0);

  // ‚Äî total ‚Äî
  const total = carrito.detalles.reduce(
    (sum,d)=>(sum + d.producto.precio_unitario * d.cantidad),0
  );

  // ‚Äî guardar direcci√≥n ‚Äî
  const guardarDireccion = async ()=>{
    if(!direccion.nombre||!direccion.calle||!direccion.ciudad||!direccion.cp){
      return alert('Completa nombre, calle, ciudad y c√≥digo postal.');
    }
    try {
      const res = await fetch(`${API}/direcciones`,{
        method:'POST',
        headers:{
          'Content-Type':'application/json',
          Authorization:`Bearer ${token}`
        },
        body: JSON.stringify({
          nombre_recibe:direccion.nombre,
          calle:direccion.calle,
          ciudad:direccion.ciudad,
          estado:direccion.estado,
          cp:direccion.cp,
          telefono:direccion.telefono,
          referencia:direccion.referencia
        })
      });
      const body = await res.json();
      if(!res.ok) throw new Error(body.msg);
      setDirecciones(ds=>[...ds,body]);
      setDireccionGuardada(true);
      setMensajeDireccion('üì¶ Direcci√≥n guardada en el servidor.');
    } catch(e){
      alert('‚ùå '+e.message);
    }
  };

  // ‚Äî guardar / eliminar m√©todo de pago ‚Äî
  const guardarMetodoPago = async ()=>{
    if(!metodoPago) return alert('Selecciona un m√©todo.');
    // si ID num√©rico ‚Üí s√≥lo seleccionamos
    if(!isNaN(+metodoPago)){
      setPagoGuardado(true);
      setMensajePago('üí≥ M√©todo de pago seleccionado.');
      return;
    }
    // nueva tarjeta
    const { numero, expiracion, cvv } = detallesPago;
    if(!numero||!expiracion||!cvv){
      return alert('Completa los datos de la tarjeta.');
    }
    try {
      const res = await fetch(`${API}/metodos`,{
        method:'POST',
        headers:{
          'Content-Type':'application/json',
          Authorization:`Bearer ${token}`
        },
        body: JSON.stringify({
          tipo:'tarjeta',
          token_last4:numero.slice(-4),
          titular:numero
        })
      });
      const body = await res.json();
      if(!res.ok) throw new Error(body.msg);
      setMetodos(ms=>[...ms,body]);
      setMetodoPago(body.id.toString());
      setPagoGuardado(true);
      setMensajePago('üí≥ Tarjeta guardada en el servidor.');
    } catch(e){
      alert('‚ùå '+e.message);
    }
  };

  const eliminarMetodo = async id=>{
    if(!window.confirm('¬øEliminar este m√©todo?'))return;
    try{
      const res = await fetch(`${API}/metodos/${id}`,{
        method:'DELETE',
        headers:{ Authorization:`Bearer ${token}` }
      });
      if(!res.ok) throw new Error('No se pudo eliminar');
      setMetodos(ms=>ms.filter(m=>m.id!==id));
      // si borraste el seleccionado, resetea select
      if(metodoPago===id.toString()){
        setMetodoPago('');
        setPagoGuardado(false);
        setMensajePago('');
      }
    }catch(e){
      alert('‚ùå '+e.message);
    }
  };

  // ‚Äî checkout ‚Äî
  const manejarPago = async ()=>{
    if(!direccionGuardada||!pagoGuardado){
      return alert('Guarda direcci√≥n y m√©todo antes.');
    }
    try{
      const res = await fetch(`${API}/carrito/checkout`,{
        method:'POST',
        headers:{
          'Content-Type':'application/json',
          Authorization:`Bearer ${token}`
        },
        body: JSON.stringify({
          direccionId: direcciones[0]?.id,
          metodoPagoId: isNaN(+metodoPago)?null:+metodoPago
        })
      });
      if(!res.ok) throw new Error('Error en checkout');
      setMensajeFinal('‚úÖ Compra procesada correctamente.');
    }catch(e){
      alert('‚ùå '+e.message);
    }
  };

  return (
    <>
      <Navbar />
      <div className="carrito-container">
        <h2>üõí Tu Carrito</h2>

        {carrito.detalles.length===0
          ? <p>Tu carrito est√° vac√≠o.</p>
          : <>
        {/* ‚Äî √≠tems ‚Äî */}
        <div className="carrito-items">
          {carrito.detalles.map(d=>(
            <div key={d.id} className="carrito-item">
              <img src={`http://localhost:4000${d.producto.imagen_url}`}
                   alt={d.producto.nombre}/>
              <div className="item-info">
                <h4>{d.producto.nombre}</h4>
                <p>${d.producto.precio_unitario.toLocaleString()} MXN</p>
                <div className="cantidad-control">
                  <label>Cantidad:</label>
                  <input type="number" min="1"
                         value={d.cantidad}
                         onChange={e=>cambiarCantidad(d.id,+e.target.value||1)}/>
                </div>
                <button className="eliminar" onClick={()=>eliminarItem(d.id)}>
                  Eliminar
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* ‚Äî direcci√≥n ‚Äî */}
        <div className="direccion-section">
          <h3>üì¶ Direcci√≥n de env√≠o</h3>
          { /* ... inputs id√©nticos a arriba ... */ }
          <button className="guardar-btn" onClick={guardarDireccion}>
            Guardar direcci√≥n
          </button>
          {mensajeDireccion && <p className="mensaje-confirmacion">{mensajeDireccion}</p>}
        </div>

        {/* ‚Äî m√©todo de pago ‚Äî */}
        <div className="metodo-pago-section">
          <h3>üí≥ M√©todo de pago</h3>
          <select value={metodoPago}
                  onChange={e=>{ setMetodoPago(e.target.value); setDetallesPago({numero:'',expiracion:'',cvv:'',email:''}); }}>
            <option value="">Selecciona un m√©todo</option>
            {/* efectivo siempre */}
            <option value="efectivo">Efectivo (pago al recibir)</option>
            {metodos.map(m=>(
              <option key={m.id} value={m.id}>
                {m.tipo==='tarjeta'
                  ? `Tarjeta ‚Ä¢‚Ä¢‚Ä¢‚Ä¢ ${m.token_last4}`
                  : m.tipo==='transferencia'
                    ? `PayPal (${m.titular})`
                    : m.tipo}
              </option>
            ))}
            <option value="nueva_tarjeta">+ Agregar nueva tarjeta</option>
          </select>

          {metodoPago==='nueva_tarjeta' && (
            <>{/* inputs tarjeta... */}</>
          )}

          {/* boton eliminar */}
          {!isNaN(+metodoPago) && (
            <button
              className="eliminar"
              onClick={()=>eliminarMetodo(+metodoPago)}
              style={{ marginTop: '8px' }}
            >
              üóë Eliminar este m√©todo
            </button>
          )}

          <button className="guardar-btn" onClick={guardarMetodoPago}>
            Guardar m√©todo de pago
          </button>
          {mensajePago && <p className="mensaje-confirmacion">{mensajePago}</p>}
        </div>

        {/* ‚Äî resumen / checkout ‚Äî */}
        <div className="resumen-carrito">
          <h3>Total: ${total.toLocaleString()} MXN</h3>
          <button className="pago-btn" onClick={manejarPago}>
            Proceder al pago
          </button>
          {mensajeFinal && <p className="mensaje-confirmacion">{mensajeFinal}</p>}
        </div>
        </>}
      </div>
    </>
  )
}
