import React, { useEffect, useState } from 'react';
import './MonitorPage.css';

export default function MonitorPage() {
  const [data, setData]   = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:4000/api/monitor')
      .then(res => {
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(setData)
      .catch(err => setError(err.message));
  }, []);

  if (error) return <p className="monitor-error">Error: {error}</p>;
  if (!data)  return <p className="monitor-loading">Cargando métricas…</p>;

  return (
    <div className="monitor-container">
      <h1>Monitor MySQL</h1>
      <div className="monitor-stats">
        <div className="stat-card">
          <span className="stat-label">Conexiones Activas</span>
          <span className="stat-value">{data.threadsConnected}</span>
        </div>
        <div className="stat-card">
          <span className="stat-label">Tiempo Activo</span>
          <span className="stat-value">{data.uptime}s</span>
        </div>
      </div>
    </div>
  );
}
