import React, { useEffect, useState } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  BarElement,
  CategoryScale,
  LinearScale,
  PointElement
} from 'chart.js';
import './MonitorPage.css';

ChartJS.register(LineElement, BarElement, CategoryScale, LinearScale, PointElement);

export default function MonitorPage() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('/api/monitor', { headers: { 'Authorization': `Bearer ${token}` } })
      .then(res => { if (!res.ok) throw new Error(`Error ${res.status}`); return res.json(); })
      .then(setData)
      .catch(() => setError('No se pudieron cargar las métricas.'));
  }, []);

  if (error) return <p className="monitor-error">{error}</p>;
  if (!data) return <div className="monitor-loading">Cargando métricas...</div>;

  const {
    threadsConnected,
    totalQueries,
    slowQueries,
    uptime,
    bytesSent,
    bytesReceived,
    topQueries,
    cpuUsage,
    memoryUsage,
    qps,
    cps,
    slowLog,
    securityEvents
  } = data;

  return (
    <div className="monitor-container">
      <h1 className="monitor-title">Dashboard de Monitoreo MySQL</h1>

      {/* Métricas básicas */}
      <div className="cards-grid">
        <div className="card"><h3>Conexiones activas</h3><p className="value">{threadsConnected}</p></div>
        <div className="card"><h3>Consultas totales</h3><p className="value">{totalQueries}</p></div>
        <div className="card"><h3>Consultas lentas</h3><p className="value">{slowQueries}</p></div>
        <div className="card"><h3>Tiempo activo</h3><p className="value">{uptime}s</p></div>
      </div>

      {/* Uso de recursos */}
      <section className="section-block">
        <h2>Uso de Recursos del Servidor</h2>
        <div className="cards-grid">
          <div className="card"><h3>CPU (%)</h3><p className="value">{cpuUsage}%</p></div>
          <div className="card"><h3>Memoria (MB)</h3><p className="value">{memoryUsage} MB</p></div>
        </div>
      </section>

      {/* Rendimiento de consultas */}
      <section className="section-block">
        <h2>Rendimiento de Consultas</h2>
        <div className="cards-grid">
          <div className="card"><h3>Queries / segundo</h3><p className="value">{qps.toFixed(1)}</p></div>
          <div className="card"><h3>Connections / segundo</h3><p className="value">{cps.toFixed(1)}</p></div>
        </div>
      </section>

      {/* Gráficos de transferencia y top queries */}
      <div className="charts-grid">
        <div className="chart-card">
          <h3>Bytes Enviados vs Recibidos</h3>
          <Bar
            data={{ labels: ['Enviados', 'Recibidos'], datasets: [{ label: 'Bytes', data: [bytesSent, bytesReceived], borderWidth: 1 }] }}
            options={{ maintainAspectRatio: false }}
          />
        </div>
        <div className="chart-card">
          <h3>Top 5 Consultas</h3>
          <Line
            data={{ labels: topQueries.map((_, i) => `#${i + 1}`), datasets: [{ label: 'Veces ejecutada', data: topQueries.map(q => q.count), fill: false, tension: 0.3 }] }}
            options={{ maintainAspectRatio: false }}
          />
        </div>
      </div>

      {/* Log de consultas lentas */}
      <section className="section-block">
        <h2>Log de Consultas Lentas Recientes</h2>
        <ul className="slow-log-list">
          {slowLog.map((entry, i) => (
            <li key={i}>
              <span className="time">{entry.query_time}s</span>
              <code className="query">{entry.sql_text}</code>
            </li>
          ))}
        </ul>
      </section>

      {/* Seguridad */}
      <section className="section-block">
        <h2>Intentos de Conexión Fallidos</h2>
        <ul className="security-list">
          {securityEvents.map((e, i) => (
            <li key={i}>
              <span className="time">{new Date(e.event_time).toLocaleString()}</span>
              <span className="user">{e.user}@{e.host}</span>
              <span className="error">{e.error_count} errores</span>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
