import React, { useEffect, useState } from 'react';
import Navbar from '../components/Auth/Navbar';
import './MisComprasPage.css';

export default function MisComprasPage() {
  const [ventas, setVentas]   = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError]     = useState('');
  const token = localStorage.getItem('token');
  const API   = 'http://localhost:4000/api';

  useEffect(() => {
    const fetchVentas = async () => {
      try {
        const res = await fetch(`${API}/ventas`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (!res.ok) throw new Error('Error al cargar tus compras');
        const data = await res.json();
        const arr = Array.isArray(data)
          ? data
          : Array.isArray(data.ventas)
            ? data.ventas
            : [];
        setVentas(arr);
      } catch (e) {
        console.error(e);
        setError(e.message);
      } finally {
        setLoading(false);
      }
    };
    fetchVentas();
  }, [token]);

  if (loading) {
    return (
      <>
        <Navbar />
        <div className="mis-compras-loading">
          <p>Cargando mis compras‚Ä¶</p>
        </div>
      </>
    );
  }

  if (error) {
    return (
      <>
        <Navbar />
        <div className="mis-compras-error">
          <p>‚ùå {error}</p>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="mis-compras-container">
        <h2>üì¶ Mis Compras</h2>
        {ventas.length === 0 ? (
          <p>No tienes compras registradas.</p>
        ) : (
          ventas.map(compra => (
            <div key={compra.id} className="compra-card">
              <h3>
                Compra #{compra.id} ‚Äì{' '}
                {new Date(compra.fecha).toLocaleDateString()}
              </h3>
              <p>
                <strong>Estado:</strong> {compra.estado}
              </p>
              <div className="productos">
                {(compra.detallesVenta || []).map(det => {
                  const prod = det.producto;
                  const mod  = prod.modelo;
                  const cat  = mod.categoria;
                  return (
                    <div key={det.id} className="producto">
                      <img
                        src={`http://localhost:4000${prod.imagen_url}`}
                        alt={prod.nombre}
                      />
                      <div className="producto-info">
                        <p><strong>Categor√≠a:</strong> {cat.nombre}</p>
                        <p><strong>Modelo:</strong> {mod.modelo}</p>
                        <p><strong>Precio:</strong> ${parseFloat(det.precio_unitario).toFixed(2)} MXN</p>
                      </div>
                    </div>
                  );
                })}
              </div>
              <p className="total">
                Total: ${parseFloat(compra.total).toFixed(2)} MXN
              </p>
            </div>
          ))
        )}
      </div>
    </>
  );
}
