// src/pages/AgregarProducto.jsx
import React, { useState, useEffect } from 'react';
import './AgregarProducto.css';
import Navbar from '../components/Auth/Navbar';

export default function AgregarProducto() {
  const [categorias, setCategorias] = useState([]);
  const [todosModelos, setTodosModelos] = useState([]);
  const [modelosFiltrados, setModelosFiltrados] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('');
  const [modeloSeleccionado, setModeloSeleccionado] = useState('');
  const [preview, setPreview] = useState(null);

  // 1) Cargo categorías
  useEffect(() => {
    fetch('/api/categorias')
      .then(r => r.json())
      .then(setCategorias)
      .catch(console.error);
  }, []);

  // 2) Cargo TODOS los modelos y los imprimo en consola
  useEffect(() => {
    fetch('/api/modelos')
      .then(r => r.json())
      .then(data => {
        console.log('📦 Modelos recibidos de la API:', data);
        setTodosModelos(data);
      })
      .catch(console.error);
  }, []);

  // 3) Cada vez que cambia categoría, filtro
  useEffect(() => {
    if (!categoriaSeleccionada) {
      setModelosFiltrados([]);
      setModeloSeleccionado('');
      return;
    }

    const filtrados = todosModelos.filter(m => {
      // extraigo posible id de categoría de distintas formas
      const fk =
        m.fk_categoria ??
        m.categoriaId ??
        (m.categoria && m.categoria.id) ??
        null;
      return String(fk) === categoriaSeleccionada;
    });

    console.log(
      `—> Modelos para categoría ${categoriaSeleccionada}:`,
      filtrados
    );
    setModelosFiltrados(filtrados);
    setModeloSeleccionado('');
  }, [categoriaSeleccionada, todosModelos]);

  const handleCategoriaChange = e => {
    setCategoriaSeleccionada(e.target.value);
  };

  const handleModeloChange = e => {
    setModeloSeleccionado(e.target.value);
  };

  const handleImageChange = e => {
    const file = e.target.files[0];
    if (file) setPreview(URL.createObjectURL(file));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const form = e.target;
    const fd = new FormData();
    fd.append('nombre', form.nombre.value);
    fd.append('descripcion', form.descripcion.value);
    fd.append('precio', form.precio.value);
    fd.append('existencia', form.existencia.value);
    fd.append('fk_categoria', categoriaSeleccionada);
    fd.append('fk_modelo', modeloSeleccionado);
    fd.append('imagen', form.imagen.files[0]);

    const res = await fetch('/api/productos', { method: 'POST', body: fd });
    if (res.ok) {
      alert('✅ Agregado');
      form.reset();
      setPreview(null);
      setCategoriaSeleccionada('');
      setModelosFiltrados([]);
      setModeloSeleccionado('');
    } else {
      alert('❌ Error');
    }
  };

  return (
    <div className="agregar-producto-page">
      <Navbar />
      <div className="form-container">
        <h2>Agregar Producto</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Nombre
            <input name="nombre" required />
          </label>

          <label>
            Descripción
            <textarea name="descripcion" required />
          </label>

          <label>
            Precio
            <input
              name="precio"
              type="number"
              step="0.01"
              required
            />
          </label>

          <label>
            Stock
            <input
              name="existencia"
              type="number"
              required
            />
          </label>

          <label>
            Categoría
            <select
              name="fk_categoria"
              value={categoriaSeleccionada}
              onChange={handleCategoriaChange}
              required
            >
              <option value="" disabled>
                -- Selecciona categoría --
              </option>
              {categorias.map(c => (
                <option key={c.id} value={c.id}>
                  {c.nombre}
                </option>
              ))}
            </select>
          </label>

          <label>
            Modelo
            <select
              name="fk_modelo"
              value={modeloSeleccionado}
              onChange={handleModeloChange}
              disabled={!modelosFiltrados.length}
              required
            >
              <option value="" disabled>
                { !categoriaSeleccionada
                    ? '-- Primero elige categoría --'
                    : modelosFiltrados.length
                    ? '-- Selecciona modelo --'
                    : '-- Sin modelos disponibles --'
                }
              </option>
              {modelosFiltrados.map(m => (
                <option key={m.id} value={m.id}>
                  {/* Ajusta esta propiedad si tu API usa otro nombre para el label */}
                  {m.modelo || m.nombre || `ID ${m.id}`}
                </option>
              ))}
            </select>
          </label>

          <label>
            Imagen
            <input
              name="imagen"
              type="file"
              accept="image/*"
              required
              onChange={handleImageChange}
            />
          </label>

          {preview && (
            <div className="preview-container">
              <img
                src={preview}
                alt="Vista previa"
                className="preview-image"
              />
            </div>
          )}

          <button type="submit">Guardar Producto</button>
        </form>
      </div>
    </div>
  );
}
