// frontend/src/pages/MisComprasPage.jsx

import React, { useEffect, useState } from 'react';
import Navbar from '../components/Auth/Navbar';
import './MisComprasPage.css';

const BASE_URL = 'http://localhost:4000';

export default function MisComprasPage() {
  const [ventas, setVentas]   = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError]     = useState('');
  const token = localStorage.getItem('token');
  const API   = `${BASE_URL}/api`;

  useEffect(() => {
    (async () => {
      try {
        const res = await fetch(`${API}/ventas`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (!res.ok) throw new Error('Error al cargar tus compras');
        const data = await res.json();
        setVentas(Array.isArray(data) ? data : data.ventas || []);
      } catch (e) {
        console.error('Fetch ventas error:', e);
        setError(e.message);
      } finally {
        setLoading(false);
      }
    })();
  }, [token]);

  if (loading) return (
    <>
      <Navbar />
      <p className="loading">Cargando mis compras‚Ä¶</p>
    </>
  );
  if (error) return (
    <>
      <Navbar />
      <p className="error">‚ùå {error}</p>
    </>
  );

  return (
    <>
      <Navbar />
      <div className="mis-compras-container">
        <h2>üì¶ Mis Compras</h2>

        {ventas.length === 0 ? (
          <p>No tienes compras registradas.</p>
        ) : ventas.map(compra => (
          <div key={compra.id} className="compra-card">
            <h3>
              Compra #{compra.id} ‚Äì{' '}
              {new Date(compra.fecha).toLocaleDateString()}
            </h3>
            <p>
              <strong>Estado del Pedido:</strong> {compra.estado}
            </p>

            <p>
              <strong>Direcci√≥n de Entrega:</strong>{' '}
              {compra.direccionDeVenta
                ? `${compra.direccionDeVenta.calle}, ${compra.direccionDeVenta.ciudad} ${compra.direccionDeVenta.cp}`
                : 'Recoger en tienda'}
            </p>

            <p>
              <strong>M√©todo de Pago:</strong>{' '}
              {compra.metodoDeVenta?.tipo || '‚Äî'}
            </p>

            <div className="productos">
              {(compra.detallesVenta || []).map(det => {
                const prod = det.producto;
                const mod  = prod.modelo;
                const cat  = mod?.categoria;
                // Normalizar ruta de imagen:
                const imgUrl = prod.imagen_url.startsWith('/')
                  ? `${BASE_URL}${prod.imagen_url}`
                  : `${BASE_URL}/uploads/${prod.imagen_url}`;

                return (
                  <div key={det.id} className="producto">
                    <img src={imgUrl} alt={prod.nombre} />
                    <div className="producto-info">
                      <p><strong>Producto:</strong> {prod.nombre}</p>
                      {cat && (
                        <p><strong>Categor√≠a:</strong> {cat.nombre}</p>
                      )}
                      {mod && (
                        <p><strong>Modelo:</strong> {mod.modelo}</p>
                      )}
                      <p><strong>Cantidad:</strong> {det.cantidad}</p>
                      <p>
                        <strong>Precio unitario:</strong> $
                        {parseFloat(det.precio_unitario).toFixed(2)}
                      </p>
                      <p>
                        <strong>Subtotal:</strong> $
                        {parseFloat(det.precio_total).toFixed(2)}
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>

            <p className="total">
              <strong>Total:</strong> ${parseFloat(compra.total).toFixed(2)} MXN
            </p>
          </div>
        ))}
      </div>
    </>
  );
}
