// frontend/src/pages/MonitorPage.jsx
import React, { useEffect, useState } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  BarElement,
  CategoryScale,
  LinearScale,
  PointElement
} from 'chart.js';
import './MonitorPage.css';

ChartJS.register(LineElement, BarElement, CategoryScale, LinearScale, PointElement);

export default function MonitorPage() {
  const [data, setData] = useState(null);
  const [err, setErr] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('/api/monitor', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(setData)
      .catch(() => setErr('No se pudieron cargar las métricas.'));
  }, []);

  if (err) return <p className="error">{err}</p>;
  if (!data) return <p>Cargando métricas…</p>;

  return (
    <div className="monitor-container">
      <h1>Dashboard de Monitoreo MySQL</h1>

      {/* — Status generales — */}
      <div className="cards">
        <div className="card">
          <strong>{data.threads}</strong><span>Conexiones activas</span>
        </div>
        <div className="card">
          <strong>{data.totalQueries}</strong><span>Consultas totales</span>
        </div>
        <div className="card">
          <strong>{data.slowQueries}</strong><span>Consultas lentas</span>
        </div>
        <div className="card">
          <strong>{data.uptime}s</strong><span>Tiempo activo</span>
        </div>
      </div>

      {/* — Uso Recursos — */}
      <h2>Uso de Recursos del Servidor</h2>
      <div className="resource-cards">
        <div className="card">
          <strong>{data.cpu}%</strong><span>CPU</span>
        </div>
        <div className="card">
          <strong>{data.memoryMB} MB</strong><span>Memoria</span>
        </div>
      </div>

      {/* — Rendimiento — */}
      <h2>Rendimiento de Consultas</h2>
      <div className="perf-cards">
        <div className="card">
          <strong>{data.qps}</strong><span>Queries / segundo</span>
        </div>
        <div className="card">
          <strong>{data.cps}</strong><span>Conexiones / segundo</span>
        </div>
      </div>

      {/* — Gráficas — */}
      <div className="chart-grid">
        <div className="chart">
          <Bar
            data={{
              labels: ['Enviados', 'Recibidos'],
              datasets: [{
                label: 'Transferencia (bytes)',
                data: [data.bytesSent, data.bytesReceived],
                borderWidth: 1
              }]
            }}
            options={{ responsive: true }}
          />
        </div>
        <div className="chart">
          <Line
            data={{
              labels: data.topQueries.map((_,i) => `#${i+1}`),
              datasets: [{
                label: 'Top 5 consultas',
                data: data.topQueries.map(q => q.count),
                fill: false, tension: 0.3
              }]
            }}
            options={{ responsive: true }}
          />
        </div>
      </div>

      {/* — Detalle top-5 — */}
      <section className="top-queries">
        <h2>Detalle de las 5 Consultas más frecuentes</h2>
        <ul>
          {data.topQueries.map((q,i) => (
            <li key={i}>
              <strong>{q.count}x</strong> {q.query}
            </li>
          ))}
        </ul>
      </section>

      {/* — Slow log reciente — */}
      <section className="slow-log">
        <h2>Log de Consultas Lentas Recientes</h2>
        <ul>
          {data.slowLogs.map((l,i) => (
            <li key={i}>
              [{new Date(l.time).toLocaleTimeString()}] <em>({l.duration}s)</em> — {l.query}
            </li>
          ))}
        </ul>
      </section>

      {/* — Intentos fallidos (esqueleto) — */}
      <section className="failed-log">
        <h2>Intentos de Conexión Fallidos</h2>
        <ul>
          {data.failedLogins.length
            ? data.failedLogins.map((f,i) => <li key={i}>{f}</li>)
            : <li>No hay intentos fallidos registrados.</li>}
        </ul>
      </section>
    </div>
  );
}
