import React, { useState } from 'react';
import './AgregarProducto.css';
import Navbar from '../components/Auth/Navbar';

const AgregarProducto = () => {
  const [nombre, setNombre] = useState('');
  const [precio, setPrecio] = useState('');
  const [fk_categoria, setCategoria] = useState('');
  const [fk_modelo, setModelo] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [existencia, setExistencia] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const nuevoProducto = {
      nombre,
      descripcion,
      precio: parseFloat(precio),
      existencia: parseInt(existencia),
      fk_categoria: parseInt(fk_categoria),
      fk_modelo: parseInt(fk_modelo)
    };

    try {
      const res = await fetch('/api/productos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(nuevoProducto)
      });

      if (!res.ok) throw new Error('Error al guardar');

      alert('✅ Producto agregado correctamente');
      setNombre('');
      setPrecio('');
      setCategoria('');
      setModelo('');
      setDescripcion('');
      setExistencia('');
    } catch (err) {
      console.error(err);
      alert('❌ Hubo un error al guardar el producto');
    }
  };

  return (
    <div className="agregar-producto-page">
      <Navbar />
      <div className="form-container">
        <h2>Agregar Nuevo Producto</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Nombre:
            <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
          </label>

          <label>
            Descripción:
            <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required />
          </label>

          <label>
            Precio:
            <input type="number" step="0.01" value={precio} onChange={(e) => setPrecio(e.target.value)} required />
          </label>

          <label>
            Existencia:
            <input type="number" value={existencia} onChange={(e) => setExistencia(e.target.value)} required />
          </label>

          <label>
            Categoría (ID):
            <input type="number" value={fk_categoria} onChange={(e) => setCategoria(e.target.value)} required />
          </label>

          <label>
            Modelo (ID):
            <input type="number" value={fk_modelo} onChange={(e) => setModelo(e.target.value)} required />
          </label>

          <button type="submit">Guardar Producto</button>
        </form>
      </div>
    </div>
  );
};

export default AgregarProducto;
