import React, { useState, useEffect } from 'react';
import './AgregarProducto.css';
import Navbar from '../components/Auth/Navbar';

const AgregarProducto = () => {
  const [categorias, setCategorias] = useState([]);
  const [modelos, setModelos] = useState([]);

  useEffect(() => {
    fetch('/api/categorias')
      .then(res => res.json())
      .then(setCategorias)
      .catch(err => console.error('Error cargando categorías:', err));

    fetch('/api/modelos')
      .then(res => res.json())
      .then(setModelos)
      .catch(err => console.error('Error cargando modelos:', err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData();

    formData.append('nombre', form.nombre.value);
    formData.append('descripcion', form.descripcion.value);
    formData.append('precio', form.precio.value);
    formData.append('existencia', form.existencia.value);
    formData.append('fk_categoria', form.fk_categoria.value);
    formData.append('fk_modelo', form.fk_modelo.value);
    formData.append('imagen', form.imagen.files[0]);

    try {
      const res = await fetch('/api/productos', {
        method: 'POST',
        body: formData
      });

      if (!res.ok) throw new Error('Error al guardar');

      alert('✅ Producto agregado correctamente');
      form.reset();
    } catch (err) {
      console.error(err);
      alert('❌ Error al guardar producto');
    }
  };

  return (
    <div className="agregar-producto-page">
      <Navbar />
      <div className="form-container">
        <h2>Agregar Nuevo Producto</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Nombre:
            <input name="nombre" type="text" required />
          </label>

          <label>
            Descripción:
            <textarea name="descripcion" required />
          </label>

          <label>
            Precio:
            <input name="precio" type="number" step="0.01" required />
          </label>

          <label>
            Existencia:
            <input name="existencia" type="number" required />
          </label>

          <label>
            Categoría:
            <select name="fk_categoria" required>
              <option value="">Seleccione una categoría</option>
              {categorias.map(cat => (
                <option key={cat.id} value={cat.id}>{cat.nombre}</option>
              ))}
            </select>
          </label>

          <label>
            Modelo:
            <select name="fk_modelo" required>
              <option value="">Seleccione un modelo</option>
              {modelos.map(mod => (
                <option key={mod.id} value={mod.id}>{mod.modelo}</option>
              ))}
            </select>
          </label>

          <label>
            Imagen:
            <input name="imagen" type="file" accept="image/*" required />
          </label>

          <button type="submit">Guardar Producto</button>
        </form>
      </div>
    </div>
  );
};

export default AgregarProducto;
