// frontend/src/components/Auth/Login.jsx
export default function Login({ onSubmit, error }) {
  const [formData, setFormData] = useState({ usuario: '', password: '' });
  const [errors, setErrors]     = useState({});

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    const errs = {};
    if (!formData.usuario) errs.usuario = 'Obligatorio';
    if (!formData.password) errs.password = 'Obligatorio';

    if (Object.keys(errs).length) {
      setErrors(errs);
    } else {
      setErrors({});
      onSubmit(e);  // aquí se invoca handleLogin
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="usuario"
        placeholder="Usuario o correo"
        onChange={handleChange}
        value={formData.usuario}
      />
      {errors.usuario && <span>{errors.usuario}</span>}

      <input
        type="password"
        name="password"
        placeholder="Contraseña"
        onChange={handleChange}
        value={formData.password}
      />
      {errors.password && <span>{errors.password}</span>}

      <button type="submit">Entrar</button>
      {error && <span>{error}</span>}
    </form>
  );
}
