// frontend/src/pages/MonitorPage.jsx
import React, { useEffect, useState } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  BarElement,
  CategoryScale,
  LinearScale,
  PointElement
} from 'chart.js';
import './MonitorPage.css'; // Ver siguiente paso para estilos

ChartJS.register(LineElement, BarElement, CategoryScale, LinearScale, PointElement);

export default function MonitorPage() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('/api/monitor')
      .then(res => res.json())
      .then(setData)
      .catch(console.error);
  }, []);

  if (!data) return <p>Cargando métricas...</p>;

  return (
    <div className="monitor-container">
      <h1>Dashboard de Monitoreo MySQL</h1>

      {/* Tarjetas resumen */}
      <div className="cards">
        <div className="card">
          <strong>{data.threadsConnected}</strong>
          <span>Conexiones activas</span>
        </div>
        <div className="card">
          <strong>{data.totalQueries}</strong>
          <span>Consultas totales</span>
        </div>
        <div className="card">
          <strong>{data.slowQueries}</strong>
          <span>Consultas lentas</span>
        </div>
        <div className="card">
          <strong>{data.uptime}s</strong>
          <span>Tiempo activo</span>
        </div>
      </div>

      {/* Gráfico de bytes enviados/recibidos */}
      <div className="chart">
        <Bar
          data={{
            labels: ['Bytes enviados', 'Bytes recibidos'],
            datasets: [{ label: 'Transferencia', data: [data.bytesSent, data.bytesReceived] }]
          }}
          options={{ responsive: true }}
        />
      </div>

      {/* Gráfico de top 5 consultas */}
      <div className="chart">
        <Line
          data={{
            labels: data.topQueries.map((_, i) => `#${i + 1}`),
            datasets: [{ label: 'Top consultas', data: data.topQueries.map(q => q.count), fill: false }]
          }}
          options={{ responsive: true }}
        />
      </div>

      {/* Lista de queries */}
      <section className="top-queries">
        <h2>Top 5 Consultas más frecuentes</h2>
        <ul>
          {data.topQueries.map((q, i) => (
            <li key={i}>
              <strong>{q.count}x</strong> {q.query}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
